query ActiveBidsRefetchQuery {
  me {
    ...ActiveBids_me
    __id
  }
}

fragment ActiveBids_me on Me {
  lot_standings(live: true) {
    most_recent_bid {
      __id
    }
    ...ActiveBid_bid
  }
  __id
}

fragment ActiveBid_bid on LotStanding {
  is_leading_bidder
  sale {
    href
    is_live_open
    __id
  }
  most_recent_bid {
    __id
    max_bid {
      display
    }
    sale_artwork {
      artwork {
        href
        image {
          url
        }
        artist_names
        __id
      }
      counts {
        bidder_positions
      }
      highest_bid {
        display
        __id: id
      }
      lot_number
      reserve_status
      __id
    }
  }
}
