query ArtistForSaleArtworksGridQuery(
  $__id: ID!
  $count: Int!
  $cursor: String
  $filter: [ArtistArtworksFilters]
) {
  node(__id: $__id) {
    __typename
    ... on Artist {
      ...ArtistForSaleArtworksGrid_artist_3KQYpM
    }
    __id
  }
}

fragment ArtistForSaleArtworksGrid_artist_3KQYpM on Artist {
  __id
  forSaleArtworks: artworks_connection(first: $count, after: $cursor, filter: $filter, sort: partner_updated_at_desc) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        __id
        image {
          aspect_ratio
        }
        ...Artwork_artwork
        __typename
      }
      cursor
    }
  }
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  id
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
