query ArtworkQuery(
  $artworkID: String!
  $excludeArtworkIds: [String!]
) {
  artwork(id: $artworkID) {
    ...Artwork_artwork
    __id
  }
}

fragment Artwork_artwork on Artwork {
  ...ArtworkTombstone_artwork
  ...ArtworkActions_artwork
  ...ArtworkAvailability_artwork
  ...SellerInfo_artwork
  ...OtherWorks_artwork
  __id
}

fragment ArtworkTombstone_artwork on Artwork {
  title
  medium
  date
  cultural_maker
  artists {
    name
    href
    __id
  }
  dimensions {
    in
    cm
  }
  edition_of
  attribution_class {
    short_description
  }
  __id
}

fragment ArtworkActions_artwork on Artwork {
  __id
  _id
  is_saved
}

fragment ArtworkAvailability_artwork on Artwork {
  availability
  __id
}

fragment SellerInfo_artwork on Artwork {
  partner {
    name
    __id
  }
  __id
}

fragment OtherWorks_artwork on Artwork {
  id
  _id
  context {
    __typename
    ... on Node {
      __id
    }
    ... on ArtworkContextFair {
      __id
    }
  }
  ...ArtworkContextArtist_artwork
  __id
}

fragment ArtworkContextArtist_artwork on Artwork {
  ...ArtistArtworkGrid_artwork
  __id
}

fragment ArtistArtworkGrid_artwork on Artwork {
  id
  artist {
    name
    artworks_connection(first: 8, sort: PUBLISHED_AT_DESC, exclude: $excludeArtworkIds) {
      pageInfo {
        startCursor
        endCursor
        hasPreviousPage
        hasNextPage
      }
      edges {
        node {
          ...GenericGrid_artworks
          __id
          __typename
        }
        cursor
      }
    }
    __id
  }
  __id
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...ArtworkGridItem_artwork
}

fragment ArtworkGridItem_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
