query CitySectionListQuery(
  $count: Int!
  $cursor: String
  $citySlug: String!
  $partnerType: PartnerShowPartnerType
  $status: EventStatus
  $dayThreshold: Int
  $sort: PartnerShowSorts
) {
  city(slug: $citySlug) {
    ...CitySectionList_city_3BpxDU
  }
}

fragment CitySectionList_city_3BpxDU on City {
  name
  shows(includeStubShows: true, first: $count, sort: $sort, after: $cursor, partnerType: $partnerType, status: $status, dayThreshold: $dayThreshold) {
    pageInfo {
      endCursor
      hasNextPage
    }
    edges {
      node {
        id
        _id
        __id
        is_followed
        start_at
        end_at
        status
        href
        type
        name
        cover_image {
          url
        }
        exhibition_period
        partner {
          __typename
          ... on Partner {
            name
            type
          }
          ... on Node {
            __id
          }
          ... on ExternalPartner {
            __id
          }
        }
        __typename
      }
      cursor
    }
  }
}
