query ConversationsQuery(
  $count: Int!
  $cursor: String
) {
  me {
    ...Conversations_me_1G22uz
    __id
  }
}

fragment Conversations_me_1G22uz on Me {
  conversations(first: $count, after: $cursor) {
    pageInfo {
      endCursor
      hasNextPage
    }
    edges {
      node {
        id
        last_message
        ...ConversationSnippet_conversation
        __id
        __typename
      }
      cursor
    }
  }
  __id
}

fragment ConversationSnippet_conversation on Conversation {
  id
  to {
    name
  }
  last_message
  last_message_at
  unread
  items {
    item {
      __typename
      ... on Artwork {
        date
        title
        artist_names
        image {
          url
        }
      }
      ... on Show {
        fair {
          name
          __id
        }
        name
        cover_image {
          url
        }
      }
      ... on Node {
        __id
      }
    }
  }
  __id
}
