query FairArtistsRendererQuery(
  $fairID: String!
) {
  fair(id: $fairID) {
    ...FairArtists_fair
    __id
  }
}

fragment FairArtists_fair on Fair {
  id
  artists(first: 10) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      cursor
      node {
        ...ArtistListItem_artist
        sortable_id
        href
        __id
        __typename
      }
    }
  }
  __id
}

fragment ArtistListItem_artist on Artist {
  id
  __id
  name
  is_followed
  nationality
  birthday
  deathday
  image {
    url
  }
}
