query FairArtistsTestsQuery {
  fair(id: "sofa-chicago-2018") {
    ...FairArtists_fair
    __id
  }
}

fragment FairArtists_fair on Fair {
  id
  artists(first: 10) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      cursor
      node {
        ...ArtistListItem_artist
        sortable_id
        href
        __id
        __typename
      }
    }
  }
  __id
}

fragment ArtistListItem_artist on Artist {
  id
  __id
  name
  is_followed
  nationality
  birthday
  deathday
  image {
    url
  }
}
