query FairTestsQuery {
  fair(id: "sofa-chicago-2018") {
    ...Fair_fair
    __id
  }
}

fragment Fair_fair on Fair {
  id
  ...FairDetail_fair
  __id
}

fragment FairDetail_fair on Fair {
  ...FairHeader_fair
  id
  name
  hours
  location {
    ...LocationMap_location
    coordinates {
      lat
      lng
    }
    __id
  }
  profile {
    name
    __id
  }
  shows: shows_connection(first: 5) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      cursor
      node {
        id
        artworks_connection(first: 4) {
          edges {
            node {
              id
              __id
            }
          }
        }
        ...FairBoothPreview_show
        __id
        __typename
      }
    }
  }
  __id
}

fragment FairHeader_fair on Fair {
  id
  name
  counts {
    artists
    partners
  }
  partner_names: shows_connection(first: 2) {
    edges {
      node {
        partner {
          __typename
          ... on Partner {
            profile {
              name
              href
              __id
            }
          }
          ... on Node {
            __id
          }
          ... on ExternalPartner {
            __id
          }
        }
        __id
      }
    }
  }
  artists_names: artists(first: 2) {
    edges {
      node {
        name
        href
        __id
      }
    }
  }
  image {
    image_url
    aspect_ratio
    url
  }
  profile {
    icon {
      id
      href
      height
      width
      url
    }
    __id
    id
    name
    is_followed
  }
  start_at
  end_at
  __id
}

fragment LocationMap_location on Location {
  __id
  id
  city
  address
  address_2
  postal_code
  coordinates {
    lat
    lng
  }
  day_schedules {
    start_time
    end_time
    day_of_week
  }
}

fragment FairBoothPreview_show on Show {
  id
  name
  is_fair_booth
  partner {
    __typename
    ... on Partner {
      name
      href
    }
    ... on ExternalPartner {
      name
      __id
    }
    ... on Node {
      __id
    }
  }
  fair {
    name
    __id
  }
  cover_image {
    url
  }
  location {
    display
    __id
  }
  artworks_connection(first: 4) {
    edges {
      node {
        ...GenericGrid_artworks
        __id
      }
    }
  }
  __id
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
