query FavoriteArtistsQuery {
  me {
    ...Artists_me
    __id
  }
}

fragment Artists_me on Me {
  followed_artists_connection(first: 10) {
    pageInfo {
      endCursor
      hasNextPage
    }
    edges {
      node {
        artist {
          id
          __id
          name
          href
          image {
            url
          }
        }
        __id
        __typename
      }
      cursor
    }
  }
  __id
}
