query GeneRefetchQuery(
  $geneID: String!
  $sort: String
  $medium: String
  $price_range: String
) {
  gene(id: $geneID) {
    ...Gene_gene_1GUEBN
    __id
  }
}

fragment Gene_gene_1GUEBN on Gene {
  ...Header_gene
  ...About_gene
  filtered_artworks(size: 0, medium: $medium, price_range: $price_range, sort: $sort, aggregations: [MEDIUM, PRICE_RANGE, TOTAL], for_sale: true) {
    total
    aggregations {
      slice
      counts {
        id
        name
        count
        __id
      }
    }
    ...GeneArtworksGrid_filtered_artworks_2GL9EE
    __id
  }
  __id
}

fragment Header_gene on Gene {
  _id
  id
  name
  __id
}

fragment About_gene on Gene {
  ...Biography_gene
  trending_artists {
    ...RelatedArtists_artists
    __id
  }
  __id
}

fragment GeneArtworksGrid_filtered_artworks_2GL9EE on FilterArtworks {
  __id
  artworks: artworks_connection(first: 10, after: "", sort: $sort) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        __id
        image {
          aspect_ratio
        }
        ...Artwork_artwork
        __typename
      }
      cursor
    }
  }
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  id
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}

fragment Biography_gene on Gene {
  description
  __id
}

fragment RelatedArtists_artists on Artist {
  __id
  ...RelatedArtist_artist
}

fragment RelatedArtist_artist on Artist {
  href
  name
  counts {
    for_sale_artworks
    artworks
  }
  image {
    url(version: "large")
  }
  __id
}
