query QueryRenderersConversationQuery(
  $conversationID: String!
) {
  me {
    ...Conversation_me
    __id
  }
}

fragment Conversation_me on Me {
  conversation(id: $conversationID) {
    id
    __id
    to {
      name
      initials
    }
    from {
      email
    }
    last_message_id
    ...Messages_conversation
    initial_message
    unread
  }
  __id
}

fragment Messages_conversation on Conversation {
  __id
  id
  from {
    name
    email
    initials
  }
  to {
    name
    initials
  }
  initial_message
  messages(first: 10, sort: DESC) {
    pageInfo {
      startCursor
      endCursor
      hasPreviousPage
      hasNextPage
    }
    edges {
      cursor
      node {
        __id
        impulse_id
        is_from_user
        body
        attachments {
          id
        }
        ...Message_message
        __typename
      }
    }
  }
  items {
    artwork: item {
      __typename
      ... on Artwork {
        href
        ...ArtworkPreview_artwork
      }
      ... on Node {
        __id
      }
    }
    show: item {
      __typename
      ... on Show {
        href
        ...ShowPreview_show
      }
      ... on Node {
        __id
      }
    }
  }
}

fragment Message_message on Message {
  body
  created_at
  is_from_user
  from {
    name
    email
  }
  invoice {
    payment_url
    ...InvoicePreview_invoice
    __id
  }
  attachments {
    id
    content_type
    download_url
    file_name
    ...ImagePreview_attachment
    ...PDFPreview_attachment
  }
  __id
}

fragment ArtworkPreview_artwork on Artwork {
  id
  _id
  title
  artist_names
  date
  image {
    url
  }
  __id
}

fragment ShowPreview_show on Show {
  id
  _id
  name
  cover_image {
    url
  }
  fair {
    name
    __id
  }
  partner {
    __typename
    ... on Partner {
      name
    }
    ... on Node {
      __id
    }
    ... on ExternalPartner {
      __id
    }
  }
  __id
}

fragment InvoicePreview_invoice on Invoice {
  payment_url
  state
  total
  lewitt_invoice_id
  __id
}

fragment ImagePreview_attachment on Attachment {
  download_url
  ...AttachmentPreview_attachment
}

fragment PDFPreview_attachment on Attachment {
  file_name
  ...AttachmentPreview_attachment
}

fragment AttachmentPreview_attachment on Attachment {
  id
}
