query QueryRenderersInboxQuery {
  me {
    ...Inbox_me
    __id
  }
}

fragment Inbox_me on Me {
  lot_standings(live: true) {
    most_recent_bid {
      __id
    }
  }
  conversations_existence_check: conversations(first: 1) {
    edges {
      node {
        id
        __id
      }
    }
  }
  ...Conversations_me
  ...ActiveBids_me
  __id
}

fragment Conversations_me on Me {
  conversations(first: 10, after: "") {
    pageInfo {
      endCursor
      hasNextPage
    }
    edges {
      node {
        id
        last_message
        ...ConversationSnippet_conversation
        __id
        __typename
      }
      cursor
    }
  }
  __id
}

fragment ActiveBids_me on Me {
  lot_standings(live: true) {
    most_recent_bid {
      __id
    }
    ...ActiveBid_bid
  }
  __id
}

fragment ActiveBid_bid on LotStanding {
  is_leading_bidder
  sale {
    href
    is_live_open
    __id
  }
  most_recent_bid {
    __id
    max_bid {
      display
    }
    sale_artwork {
      artwork {
        href
        image {
          url
        }
        artist_names
        __id
      }
      counts {
        bidder_positions
      }
      highest_bid {
        display
        __id: id
      }
      lot_number
      reserve_status
      __id
    }
  }
}

fragment ConversationSnippet_conversation on Conversation {
  id
  to {
    name
  }
  last_message
  last_message_at
  unread
  items {
    item {
      __typename
      ... on Artwork {
        date
        title
        artist_names
        image {
          url
        }
      }
      ... on Show {
        fair {
          name
          __id
        }
        name
        cover_image {
          url
        }
      }
      ... on Node {
        __id
      }
    }
  }
  __id
}
