query SaleArtworksGridQuery(
  $__id: ID!
  $count: Int!
  $cursor: String
) {
  node(__id: $__id) {
    __typename
    ... on Sale {
      ...SaleArtworksGrid_sale_1G22uz
    }
    __id
  }
}

fragment SaleArtworksGrid_sale_1G22uz on Sale {
  __id
  saleArtworks: sale_artworks_connection(first: $count, after: $cursor) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        artwork {
          id
          __id
          image {
            aspect_ratio
          }
          ...Artwork_artwork
        }
        __id
        __typename
      }
      cursor
    }
  }
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  id
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
