query ShowTestsQuery {
  show(id: "anderson-fine-art-gallery-flickinger-collection") {
    ...Show_show
    __id
  }
}

fragment Show_show on Show {
  ...Detail_show
  ...MoreInfo_show
  ...ShowArtists_show
  ...ShowArtworks_show
  __id
}

fragment Detail_show on Show {
  id
  name
  description
  city
  location {
    id
    address
    address_2
    displayDaySchedules {
      days
      hours
    }
    __id
    ...LocationMap_location
  }
  images {
    id
  }
  ...ShowHeader_show
  ...ShowArtworksPreview_show
  ...ShowArtistsPreview_show
  ...Shows_show
  counts {
    artworks
  }
  status
  partner {
    __typename
    ... on ExternalPartner {
      name
      __id
    }
    ... on Partner {
      name
      type
    }
    ... on Node {
      __id
    }
  }
  __id
}

fragment MoreInfo_show on Show {
  partner {
    __typename
    ... on Partner {
      website
      type
    }
    ... on Node {
      __id
    }
    ... on ExternalPartner {
      __id
    }
  }
  press_release
  events {
    ...EventSection_event
  }
  __id
}

fragment ShowArtists_show on Show {
  artists_grouped_by_name {
    letter
    items {
      ...ArtistListItem_artist
      __id
    }
  }
  __id
}

fragment ShowArtworks_show on Show {
  __id
  filteredArtworks(size: 0, medium: "*", price_range: "*-*", aggregations: [MEDIUM, PRICE_RANGE, TOTAL]) {
    ...FilteredInfiniteScrollGrid_filteredArtworks
    __id
  }
}

fragment FilteredInfiniteScrollGrid_filteredArtworks on FilterArtworks {
  ...Filters_filteredArtworks
  ...ArtworksGridPaginationContainer_filteredArtworks
  __id
}

fragment Filters_filteredArtworks on FilterArtworks {
  aggregations {
    slice
    counts {
      id
      name
      __id
    }
  }
  __id
}

fragment ArtworksGridPaginationContainer_filteredArtworks on FilterArtworks {
  __id
  artworks: artworks_connection(first: 10) {
    pageInfo {
      hasNextPage
      startCursor
      endCursor
    }
    edges {
      node {
        id
        __id
        image {
          aspect_ratio
        }
        ...Artwork_artwork
        __typename
      }
      cursor
    }
  }
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}

fragment ArtistListItem_artist on Artist {
  id
  __id
  name
  is_followed
  nationality
  birthday
  deathday
  image {
    url
  }
}

fragment EventSection_event on PartnerShowEventType {
  event_type
  description
  start_at
  end_at
}

fragment LocationMap_location on Location {
  __id
  id
  city
  address
  address_2
  postal_code
  coordinates {
    lat
    lng
  }
  day_schedules {
    start_time
    end_time
    day_of_week
  }
}

fragment ShowHeader_show on Show {
  id
  __id
  name
  press_release
  is_followed
  exhibition_period
  status
  partner {
    __typename
    ... on Partner {
      name
    }
    ... on ExternalPartner {
      name
      __id
    }
    ... on Node {
      __id
    }
  }
  images {
    url
    aspect_ratio
  }
  artists {
    name
    href
    __id
  }
}

fragment ShowArtworksPreview_show on Show {
  __id
  artworks(size: 6) {
    ...GenericGrid_artworks
    __id
  }
  counts {
    artworks
  }
}

fragment ShowArtistsPreview_show on Show {
  artists {
    id
    href
    ...ArtistListItem_artist
    __id
  }
  __id
}

fragment Shows_show on Show {
  nearbyShows(first: 20) {
    edges {
      node {
        ...ShowItem_show
        __id
      }
    }
  }
  __id
}

fragment ShowItem_show on Show {
  __id
  id
  name
  exhibition_period
  images {
    url
    aspect_ratio
  }
  partner {
    __typename
    ... on ExternalPartner {
      name
      __id
    }
    ... on Partner {
      name
    }
    ... on Node {
      __id
    }
  }
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}
