query indexTestsFairBothPreviewQuery {
  show(id: "abxy-blk-and-blue") {
    ...FairBoothPreview_show
    __id
  }
}

fragment FairBoothPreview_show on Show {
  id
  _id
  name
  is_fair_booth
  counts {
    artworks
  }
  partner {
    __typename
    ... on Partner {
      name
      href
      id
      _id
      __id
      profile {
        _id
        is_followed
        __id
      }
    }
    ... on Node {
      __id
    }
    ... on ExternalPartner {
      __id
    }
  }
  fair {
    name
    __id
  }
  cover_image {
    url
  }
  location {
    display
    __id
  }
  artworks_connection(first: 4) {
    edges {
      node {
        ...GenericGrid_artworks
        __id
      }
    }
  }
  __id
}

fragment GenericGrid_artworks on Artwork {
  __id
  id
  image {
    aspect_ratio
  }
  ...Artwork_artwork
}

fragment Artwork_artwork on Artwork {
  title
  date
  sale_message
  is_in_auction
  is_biddable
  is_acquireable
  is_offerable
  id
  sale {
    is_auction
    is_live_open
    is_open
    is_closed
    display_timely_at
    __id
  }
  sale_artwork {
    opening_bid {
      display
    }
    current_bid {
      display
    }
    bidder_positions_count
    sale {
      is_closed
      __id
    }
    __id
  }
  image {
    url(version: "large")
    aspect_ratio
  }
  artists(shallow: true) {
    name
    __id
  }
  partner {
    name
    __id
  }
  href
  __id
}
